import{_ as t,c as i,o,ag as a}from"./chunks/framework.C6oxWInS.js";const u=JSON.parse('{"title":"Git","description":"","frontmatter":{"outline":"deep","title":"Git"},"headers":[],"relativePath":"installations/git.md","filePath":"installations/git.md"}'),l={name:"installations/git.md"};function n(s,e,r,d,c,h){return o(),i("div",null,e[0]||(e[0]=[a('<h1 id="git" tabindex="-1">Git <a class="header-anchor" href="#git" aria-label="Permalink to &quot;Git&quot;">​</a></h1><h2 id="what-is-git" tabindex="-1">What is Git <a class="header-anchor" href="#what-is-git" aria-label="Permalink to &quot;What is Git&quot;">​</a></h2><p>Git is a distributed version control system that records every change you make to your code, lets you rewind to any previous state, and supports branching so you can safely experiment without affecting your “main” work.</p><p>Why we use Git (and GitHub) instead of Google Classroom:</p><ul><li><p>Real-world practice: Professional developers worldwide rely on Git daily. By learning Git here, you acquire skills you’ll use in internships, labs, and future jobs.</p></li><li><p>Instant, automated feedback: Every push triggers our Continuous Integration checks (tests, style reviews), so you see errors or suggestions immediately rather than waiting for manual grading.</p></li><li><p>Complete history &amp; easy rollback: Unlike a one-off upload, Git preserves the full history of your edits. Made a mistake? You can revert to any prior version with a single command.</p></li><li><p>Collaboration &amp; peer review: GitHub’s pull-request workflow lets you review classmates’ code, discuss improvements inline, and learn from each other in a transparent, controlled way.</p></li></ul><h2 id="installing-git" tabindex="-1">Installing Git <a class="header-anchor" href="#installing-git" aria-label="Permalink to &quot;Installing Git&quot;">​</a></h2><ol><li><p>Go to <a href="https://git-scm.com/download/win" target="_blank" rel="noreferrer">https://git-scm.com/download/win</a> — the download should begin automatically.</p></li><li><p>Double-click the downloaded <code>Git-version-64-bit.exe</code> file and click Next on the welcome screen.</p></li><li><p>Select Destination Location: You can accept the default or choose any locations that you like.</p></li><li><p>Select Components: Leave everything as is.</p></li><li><p>Choosing the default editor used by Git : Choose the default.</p></li><li><p>Adjusting the name of the initial branch in new repositories : Choose the default.</p></li><li><p>Adjusting your <code>PATH</code> environment: Choose <code>Git from the command line and also from 3rd-party software.</code></p></li><li><p>Choosing the SSH executable : Choose the default.</p></li><li><p>Choosing HTTPS transport backend: Leave <code>Use the OpenSSL library</code></p></li><li><p>Configuring line endings: Choose <code>Checkout Windows-style, commit Unix-style line endings.</code></p></li><li><p>Configuring the terminal emulator to use with Git Bash: Leave <code>Use MinTTY.</code></p></li><li><p>Choose the default behaviour of git pull : Choose the default.</p></li><li><p>Choose the credential helper : Choose <code>Git Credential Manager</code>.</p></li><li><p>Configuring extra options: Choose the default.</p></li><li><p>Click <code>install</code>, it should take a few minutes you are good to go!</p></li></ol>',7)]))}const g=t(l,[["render",n]]);export{u as __pageData,g as default};
